        for i in range(target + 1):
            for j in range(1, count + 1):
                if i + j * marks <= target:
                    new_dp[i + j * marks] = (new_dp[i + j * marks] + dp[i]) % MOD
                else:
                    break
        dp = new_dp

    return dp[target]

